import { Box } from '@mui/system';


const ToppingIcon = ({ topping, mediaQuery, t }) => {
    return (
        <Box
        sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            margin: '1rem 0.1rem',
            padding: '0',
            width: '30%',
            height: "auto",
            maxWidth: (mediaQuery) ? '200px' : '100px',
            maxHeight: (mediaQuery) ? '200px' : '125px',
        }}
        >
            <img
                src={topping.src}
                alt={topping.name}
                style={{
                    display: "block",
                    width: (mediaQuery) ? '125px' : '75px',
                    height: (mediaQuery) ? '125px' : '75px',
                    objectFit: "cover",
                    borderRadius: "50%",
                }}
            />
            <p
                style={{
                    margin: '0.5rem 0',
                    padding: '0',
                    fontSize: mediaQuery ? '1rem' : '0.75rem',
                    textAlign: 'center',
                    fontWeight: 'bold',
                }}
            >
                {t(topping.name)}
            </p>
        </Box>
    );
}

export default ToppingIcon;
