import { Typography, Box, Switch, Link } from "@mui/material";
import colors from "../utils/colors";


const Nav = ({mediaQuery, changeLanguage, language, links}) => {

    const handleLanguageChange = () => {
        if (language === 'es') {
            changeLanguage('en');
        }else{
            changeLanguage('es');
        }
    }

    return (
        <Box
            width="100%"
            display="flex"
            border="1px solid black"
            justifyContent="space-between"
            alignItems="center"
        >
            <img src={`${process.env.PUBLIC_URL}/img/logo.png`} alt="Logo" 
                style={{ width: '25%', height: '25%', maxWidth: "25vw", maxHeight: "10vh",  border: `1px solid ${colors.bluey}`}} 
            />

            <Box
                border={`1px solid ${colors.bluey}`}
                width="25%"
            >
                {Object.keys(links).map((link, index) => {
                    return (
                        <Link key={index} href={links[link]} underline="none">
                            {link}
                        </Link>
                    );
                })}
            </Box>

            <Box
                display="flex"
                justifyContent="center"
                alignItems="center"
                border={`1px solid ${colors.bluey}`}
                width="25%"
            >
                <Typography>
                    Esp
                </Typography>
                <Switch 
                    checked={language === 'en'}
                    onChange={handleLanguageChange}
                    color="success"
                />
                <Typography>
                    Eng
                </Typography>
            </Box>
        </Box>
    );
}

export default Nav;
