import { Box, Typography } from '@mui/material';
import Slider from '../widgets/Slider';

const Toppings = ({mediaQuery}) => {
    return (
        <Box
            width="100%"
            display="flex"
            backgroundColor="lightblue"
            margin="1rem"
            sx={{padding: (mediaQuery ? '1.5rem' : '1rem')}}
            border="1px solid black"
            justifyContent="center"
        >
            <Box
                width="90%"
                backgroundColor="lightgreen"
                padding="1rem"
                border="1px solid black"
                display="flex"
                justifyContent="space-around"
                alignItems="center"
                sx={{flexDirection: (mediaQuery ? 'row' : 'column-reverse')}}
            >
                <Typography
                    variant="h3"
                >Toppings a elegir</Typography>
                <Typography>Elige entre los mas de 40 toppings que podemos ofrecerte, Sabias que puedes 450,000 combinaciones de toppings</Typography>

            </Box>
        </Box>
    );
}

export default Toppings;
