import "@egjs/react-flicking/dist/flicking.css";
import { Box } from "@mui/material";
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.min.css';
import 'swiper/swiper.min.css';
import SwiperCore, {
  Autoplay,
  Grid,
  FreeMode,
  Loop
} from 'swiper';

const Slider = () => {

    const images = [
        {
            name: "Almendra",
            src: `${process.env.PUBLIC_URL}/img/toppings/almendra.jpg`
        },
        {
            name: "Amaranto",
            src: `${process.env.PUBLIC_URL}/img/toppings/amaranto.jpg`
        },
        {
            name: "Blueberries",
            src: `${process.env.PUBLIC_URL}/img/toppings/blueberries.jpg`
        },
        {
            name: "Boba",
            src: `${process.env.PUBLIC_URL}/img/toppings/boba.avif`
        },
        {
            name: "Cajeta",
            src: `${process.env.PUBLIC_URL}/img/toppings/cajeta.jpeg`
        },
        {
            name: "Captain Crunch",
            src: `${process.env.PUBLIC_URL}/img/toppings/captain.jpg`
        },
        {
            name: "Cherry",
            src: `${process.env.PUBLIC_URL}/img/toppings/cherry.jpeg`
        }
    ]

    return (
        <Box
            width="100%"
        >
            <Swiper
            autoplay={{ delay: 3000 }} // Configura autoplay
            loop={true} // Habilita el loop infinito
            slidesPerView={3} // Número de slides visibles a la vez
            grid={{ rows: 2 }} // Configuración de grid
            freeMode={true} // Habilita free mode
            spaceBetween={10} // Espacio entre slides
            modules={[Autoplay, Grid, FreeMode]}
            className="mySwiper"
            >
            {images.map((image, index) => (
                <SwiperSlide key={index}>
                <img
                    src={image.img}
                    alt={image.title}
                    style={{
                    width: '100%',
                    height: 'auto',
                    }}
                />
                </SwiperSlide>
            ))}
            </Swiper>
        </Box>

    )
}

export default Slider;
