import { useTranslation } from 'react-i18next';
import { Box, useMediaQuery } from '@mui/material';
import Nav from './components/Nav';
import { useState } from 'react';
import MainAnnouncement from './components/MainAnnouncement';
import Qualities from './components/Qualities';
import OpeningHours from './components/OpeningHours';
import Flavors from './components/Flavors';
import Toppings from './components/Toppings';
import CustomerReview from './components/CustomerReview';
import Footer from './components/Footer';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

function App() {
  const mediaQuery = useMediaQuery('(min-width:1000px)')

  const { t, i18n } = useTranslation();

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    console.log('Language changed to: ', i18n.language);
  };

  const [links, setLinks] = useState({
    home: {
      href: '/',
      nameRef: 'Home'
    },
    flavors: {
      href: '/flavors',
      nameRef: 'Flavors'
    },
    reviews: {
      href: '/reviews',
      nameRef: 'Reviews'
    },

  });

  console.log(links["home"]["nameRef"]);

  return (
    <Router>
    <Box 
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        margin: '0',
        padding: '0',
        width: '100%',
        border: '1px solid black',
      }}
    >
      <Nav mediaQuery={mediaQuery} language={i18n.language} changeLanguage={changeLanguage} links={links} t={t}/>
      <Routes>
          <Route
            path="/"
            element={
              <>
                <MainAnnouncement mediaQuery={mediaQuery} t={t} />
                <Qualities mediaQuery={mediaQuery} t={t} />
                <OpeningHours mediaQuery={mediaQuery} t={t} />
                <Flavors mediaQuery={mediaQuery} t={t} />
                <Toppings mediaQuery={mediaQuery} t={t} />
                <CustomerReview mediaQuery={mediaQuery} language={i18n.language} t={t} />
              </>
            }
          />
          <Route path="/flavors" element={<Flavors mediaQuery={mediaQuery} t={t} />} />
          <Route path="/reviews" element={<CustomerReview mediaQuery={mediaQuery} t={t} language={i18n.language} />} />
        </Routes>

      <Footer mediaQuery={mediaQuery}></Footer>



    </Box>
    </Router>


  );
}

export default App;
