import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  typography: {
    special: {
      fontSize: '2rem',
      fontWeight: 700,
      fontFamily: 'Italiana, sans-serif',
    },
    h1: {
      fontSize: '20rem',
      fontWeight: 700,
    },
    h2: {
      fontSize: '1.75rem',
      fontWeight: 600,
    },
    h3: {
      fontSize: '1.5rem',
      fontWeight: 500,
    },
    body1: {
      fontSize: '1rem',
      fontWeight: 400,
    },
    body2: {
      fontSize: '0.875rem',
      fontWeight: 400,
    },
    button: {
      fontSize: '1rem',
      fontWeight: 500,
    },

  },
});

export default theme;
