import { Box } from "@mui/material";

const Footer = ({mediaQuery, t, links}) => {
    const scrollToSection = (elementRef) => {
        if (elementRef && elementRef.current) {
            elementRef.current.scrollIntoView({
                behavior: "smooth",
                block: "start",
            });
        }else{
            console.log('Pendejo');
        }
    };

    return (
        <Box
        width="100%"
        display="flex"
        backgroundColor="black"
        //sx={{padding: (mediaQuery ? '1.5rem' : '1rem')}}
        justifyContent="center"
        flexDirection="column"
        alignItems="center"
        >
            <Box
                width="90%"
                backgroundColor="lightgreen"
                padding="1rem"
                border="1px solid black"
                display="flex"
                justifyContent="space-around"
                alignItems="center"
                flexDirection="column"
            >
                <img src={`${process.env.PUBLIC_URL}/img/logoChico.jpg`} alt="sweetberry logo"
                    style={{width: (mediaQuery ? '30%' : '20%'), height: 'auto', maxWidth: "200px", margin: "1rem 0"}}
                />

                <Box
                    width="100%"
                    display="flex"
                    justifyContent="space-around"
                    alignItems="center"
                    flexDirection="row"
                >
                    {Object.keys(links).map((link, index) => {
                            return (
                                <Typography key={index} onClick={() => scrollToSection(links[link].href)}
                                    style={{
                                        margin: '0 1rem',
                                        fontSize: '1.25rem',
                                        color: "black",
                                        textDecoration: 'none',
                                        cursor: 'pointer'
                                    }}
                                >
                                    {t(link)}
                                </Typography>
                            );
                    })}
                </Box>
            </Box>
        </Box>
    );
}

export default Footer;
