import { Box, Typography } from '@mui/material';
import Slider from '../widgets/Slider';
import CardSlider from '../widgets/CardSlider';
import { useRef } from 'react';


const CustomerReview = ({mediaQuery, language, t}) => {
    const reviewsRef = useRef(null);


    return(
        <Box
            width="100%"
            display="flex"
            backgroundColor="lightblue"
            //margin="1rem"
            //sx={{padding: (mediaQuery ? '1.5rem' : '1rem')}}
            //border="1px solid black"
            justifyContent="center"
            ref={reviewsRef}
        >
            <Box
                width="90%"
                backgroundColor="lightgreen"
                padding="1rem 0"
                border="1px solid black"
                display="flex"
                justifyContent="space-around"
                alignItems="center"
                flexDirection="column"
            >
                <Typography
                    variant="h4"
                    sx={{textAlign: "center"}}
                >
                    {t('customerReviews')}
                </Typography>
                {
                    (mediaQuery ? 

                        <Slider language={language}></Slider>

                        :
                        <Box
                            width="100%"
                            display="flex"
                            justifyContent="center"
                            alignItems="center"
                            padding="1rem"
                            height="500px"
                            overflow="hidden"
                        >
                            <CardSlider language={language} mediaQuery={mediaQuery}></CardSlider>
                        </Box>

                    )
                }
            </Box>
        </Box>
    )
}

export default CustomerReview;
