import { Typography, Box, Switch } from "@mui/material";


const Nav = ({mediaQuery, changeLanguage, language}) => {

    const handleLanguageChange = () => {
        if (language === 'es') {
            changeLanguage('en');
        }else{
            changeLanguage('es');
        }
    }

    return (
        <Box
            width="100%"
            display="flex"
            border="1px solid black"
            justifyContent="center"
            alignItems="center"
        >
            <img src={`${process.env.PUBLIC_URL}/img/logo.png`} alt="Logo" 
                style={{ width: '25%', height: '25%', maxWidth: "25vw", maxHeight: "10vh" }} 
            />

            <Box
                display="flex"
                justifyContent="center"
                alignItems="center"
            >
                <Typography>
                    Esp
                </Typography>
                <Switch 
                    checked={language === 'en'}
                    onChange={handleLanguageChange}
                />
            </Box>

            <Typography variant="h1">Nav</Typography>
        </Box>
    );
}

export default Nav;
