import { Box } from '@mui/system';


const ToppingIcon = ({ topping, mediaQuery }) => {
    return (
        <Box
        sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            margin: '0 auto',
            padding: '0',
            width: '10%',
            height: '10%',
        }}
        >
            <img
                src={topping.src}
                alt={topping.name}
                style={{
                    display: "block",
                    width: "100%",
                    height: "100%",
                    objectFit: "cover"
                }}
            />
            <p
                style={{
                margin: '0',
                padding: '0',
                fontSize: mediaQuery ? '0.8rem' : '1.5rem',
                }}
            >
                {topping.name}
            </p>
        </Box>
    );
}

export default ToppingIcon;
